// Hero.js

import React from 'react';

const Hero = () => {
  return (
    <div className="bg-cover bg-center h-screen flex items-center" style={{ backgroundImage: 'url("/../public/cinema.jpg")' }}>
      <div className="text-white text-center mx-auto">
        <h1 className="text-4xl font-bold mb-4">Welcome to Our Website</h1>
        <p className="text-lg">Discover amazing content and more.</p>
      </div>
    </div>
  );
}

export default Hero;
