import React, { useEffect, useState } from 'react';

const ListMovies = ({ refreshKey }) => {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
      // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
      const fetchData = async () => {
        try {
          const response = await fetch('http://localhost:4000/api/movies');
          if (response.ok) {
            const data = await response.json();
            setMovies(data.results);
          } else {
            console.error('Failed to fetch data');
          }
        } catch (error) {
          console.error('Error:', error);
        }
      };

      fetchData();
    }, [refreshKey]); // Re-run effect when refreshKey changes

  return (
    <div className="product-list-container">
      <h2>Movies</h2>
      {movies.length === 0 ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {movies.map((movie) => (
            <li key={movie.movie_id}>
              <h3>{movie.title}</h3>
              <p>
                <strong>Genre:</strong> {movie.genre}
                <strong>Year:</strong> {movie.year_released}
                <strong>Sinopsis:</strong> {movie.sinopsis}
                <strong>Director:</strong> {movie.director}
                <strong>Casting:</strong> {movie.casting}
                <strong>Rating:</strong> {movie.rating}
              </p>
              <img src={movie.genre} width="100" height="300"></img>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ListMovies;
