import React, { useState, useEffect } from 'react';

const MovieCrud = () => {
  const [movies, setMovies] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    genre: '',
    year_released: '',
    sinopsis: '',
    director: '',
    casting: '',
    rating: '',
    cover_img: '',
  });

  useEffect(() => {
    // Fetch movies when the component mounts
    const fetchMovies = async () => {
      try {
        const response = await fetch('http://localhost:2090/api/movies'); // Replace 'your-api-endpoint' with the actual API endpoint
        if (response.ok) {
          const data = await response.json();
          setMovies(data);
        } else {
          console.error('Failed to fetch movies');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchMovies();
  }, []); // Empty dependency array to fetch movies only once

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCreate = async () => {
    try {
      const response = await fetch('your-api-endpoint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Movie created successfully');
        // Refetch movies to update the list
        const updatedMovies = await (await fetch('your-api-endpoint')).json(); // Replace 'your-api-endpoint'
        setMovies(updatedMovies);
      } else {
        console.error('Failed to create movie');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleUpdate = async (id) => {
    try {
      const response = await fetch(`your-api-endpoint/${id}`, { // Replace 'your-api-endpoint'
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Movie updated successfully');
        // Refetch movies to update the list
        const updatedMovies = await (await fetch('your-api-endpoint')).json(); // Replace 'your-api-endpoint'
        setMovies(updatedMovies);
      } else {
        console.error('Failed to update movie');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`your-api-endpoint/${id}`, { // Replace 'your-api-endpoint'
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Movie deleted successfully');
        // Refetch movies to update the list
        const updatedMovies = await (await fetch('your-api-endpoint')).json(); // Replace 'your-api-endpoint'
        setMovies(updatedMovies);
      } else {
        console.error('Failed to delete movie');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Movie CRUD</h2>

      {/* Create Movie Form */}
      <div>
        <h3>Create Movie</h3>
        <label>
          Title:
          <input type="text" name="title" value={formData.title} onChange={handleChange} />
        </label>
        {/* Add other input fields for genre, year_released, sinopsis, director, casting, rating, cover_img */}
        <button onClick={handleCreate}>Create</button>
      </div>

      {/* Display Movies */}
      <div>
        <h3>Movies</h3>
        <ul>
          {movies.map((movie) => (
            <li key={movie.id}>
              <span>{movie.title}</span>
              {/* Display other movie details */}
              <button onClick={() => handleUpdate(movie.id)}>Update</button>
              <button onClick={() => handleDelete(movie.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MovieCrud;
