import React, { useState, useEffect } from 'react';
import MovieForm from './MovieForm';

const MovieCrud = () => {
  const [movies, setMovies] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    genre: '',
    year_released: '',
    sinopsis: '',
    director: '',
    casting: '',
    rating: '',
    cover_img: '',
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch('http://localhost:2090/api/movies');
        if (response.ok) {
          const data = await response.json();
          setMovies(data);
        } else {
          console.error('Failed to fetch movies');
        }
      } catch (error) {
        console.error('Error:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCreate = async () => {
    try {
      const response = await fetch('http://localhost:2090/api/movies', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Movie created successfully');
        const updatedMovies = await (await fetch('http://localhost:2090/api/movies')).json();
        setMovies(updatedMovies);
        setFormData({
          title: '',
          genre: '',
          year_released: '',
          sinopsis: '',
          director: '',
          casting: '',
          rating: '',
          cover_img: '',
        });
      } else {
        console.error('Failed to create movie');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:2090/api/movies/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Movie deleted successfully');
        const updatedMovies = await (await fetch('http://localhost:2090/api/movies')).json();
        setMovies(updatedMovies);
      } else {
        console.error('Failed to delete movie');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Movie List</h2>
      <MovieForm
        formData={formData}
        handleChange={handleChange}
        handleSubmit={handleCreate}
        buttonText="Create Movie"
      />
      {loading ? (
        <p>Loading movies...</p>
      ) : (
        <ul>
          {movies.map((movie) => (
            <li key={movie.id}>
              <span>{movie.title}</span>
              <button>Edit</button>
              <button onClick={() => handleDelete(movie.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MovieCrud;
