// Home.js

import React from 'react';
import Header from '../components/Header';
import MovieCard from './MovieCard';

const Home = () => {

  const movies = [

    {
      title: "Jojo's: Bizarre Adventures",
      imageSrc: "https://m.media-amazon.com/images/I/81IF6cvmg8L.jpg",
      description: "Season 1",
    },
    {
      title: "Jojo's: Bizarre Adventures",
      imageSrc: "https://jjba-store.com/cdn/shop/products/product-image-1326646837_a371fd99-503a-4bce-8f16-7e7e68cf261e_452x.jpg?v=1610545073",
      description: "Season 4",
    },
    {
      title: "Jojo's: Bizarre Adventures",
      imageSrc: "https://i.ebayimg.com/images/g/WEgAAOSwUYph5Tt1/s-l1200.webp",
      description: "Season 3",
    },
    {
      title: "Jojo's: Bizarre Adventures",
      imageSrc: "https://m.media-amazon.com/images/I/71NbPUu-lWL.jpg",
      description: "Season 5",
    },
    {
      "title": "Naruto",
      "imageSrc": "https://example.com/naruto_poster.jpg",
      "description": "Season 1"
    },
    {
      "title": "Attack on Titan",
      "imageSrc": "https://example.com/aot_poster.jpg",
      "description": "Season 3"
    },
    {
      "title": "One Piece",
      "imageSrc": "https://example.com/one_piece_poster.jpg",
      "description": "Season 2"
    },
    {
      "title": "My Hero Academia",
      "imageSrc": "https://example.com/mha_poster.jpg",
      "description": "Season 4"
    },
    {
      "title": "Demon Slayer",
      "imageSrc": "https://example.com/demon_slayer_poster.jpg",
      "description": "Season 1"
    }
  ];

  return ( 
    <div>
      <Header />

      <div className="bg-gray-900 py-5">

          <div className="text-center">
            <h3 className="text-white text-2xl">All your movies and series, in one place.</h3>
          </div>

          <div className="flex flex-wrap justify-center">
            {movies.map((movie, index) => (
              <MovieCard key={index} {...movie} />
            ))}
          </div>

          <div className="text-center">
            <button className="text-white p-3 w-80 rounded-full bg-blue-600">Show all series</button>
          </div>



      </div>
    </div>
  );
}

export default Home;
