import React, { useState, useEffect } from 'react';

const MovieTable = () => {
  const [movieData, setMovieData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:2090/api/movies'); // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint
        const data = await response.json();
        setMovieData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div>
      <h1>Movies Information</h1>
      {movieData ? (
        <table>
          <thead>
            <tr>
              <th>movie_id</th>
              <th>title</th>
              <th>genre</th>
              <th>year_released</th>
              <th>sinopsis</th>
              <th>director</th>
              <th>casting</th>
              <th>rating</th>
              <th>cover_img</th>
              <th>created_at</th>
            </tr>
          </thead>
          <tbody>
            {movieData.results.map((movie) => (
              <tr key={movie.movie_id}>
                <td>{movie.movie_id}</td>
                <td>{movie.title}</td>
                <td>{movie.genre}</td>
                <td>{movie.year_released}</td>
                <td>{movie.sinopsis}</td>
                <td>{movie.director}</td>
                <td>{movie.casting}</td>
                <td>{movie.rating}</td>
                <td>
                  <img src={movie.cover_img} alt={movie.title} style={{ maxWidth: '100px', maxHeight: '100px' }} />
                </td>
                <td>{movie.created_at}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default MovieTable;
