import React, { useState, useEffect } from 'react';

const MovieTable = () => {
  const [movieData, setMovieData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:2090/api/movies'); // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint
        const data = await response.json();
        setMovieData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div>
        <div className="max-w-screen-xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8">
        {movieData ? (
          movieData.results.map((movie) => (

            <div className="bg-slate-200 rounded-xl text-center" key={movie.movie_id} >
              <img className="w-60" src={movie.cover_img} alt={movie.title} />
              <div>
                <div>{movie.title}</div>
                <p>{movie.sinopsis}</p>
              </div>
              <div>
                <span>
                  Director: {movie.director}
                </span>
                <span>
                  Rating: {movie.rating}
                </span>
              </div>
            </div>

          ))
        ) : (
          <p>Loading...</p>
        )}
        </div>
    </div>

  );
};

export default MovieTable;
