// MovieCard.js

import React from 'react';

const MovieCard = ({ title, imageSrc, description }) => {
  return (
    <div className="max-w-xs mx-auto rounded-xl overflow-hidden shadow-lg bg-gray-950 mx-3 my-3">

    <div className="relative w-full aspect-w-2 aspect-h-3">
        <img className="absolute object-cover w-full h-full" src={imageSrc} alt={title} />
        <img className="w-full h-60 object-cover" src={imageSrc} alt={title} />
      </div>

      <div className="px-6 py-4">
        <div className="font-bold text-xs mb-2 text-white">{title}</div>
        <p className="text-white text-base">{description}</p>
      </div>
    </div>
  );
};

export default MovieCard;
