// Navbar.js

import React, { useState } from 'react';
import '../index.css'; // Import the CSS file

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('home');

  const handleNavLinkClick = (link) => {
    setActiveLink(link);
  };

  return (

    <nav className="bg-transparent -mb-18 text-white p-3">
      <div className="container mx-auto flex items-center justify-between">
        <a href="/" className="text-lg font-semibold">CinePedia</a>
        <div className="flex items-center space-x-10">
          <a href="/" >Home</a>
          <a href="/movies" className="">Movies</a>
          <a href="/about" >About</a>
          <a href="/contact" >Contact</a>
        </div>
        <div className="flex items-center space-x-4">
          <button className=" hover:text-gray-300">Login</button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition">Sign Up</button>
        </div>
      </div>
    </nav>

  );
}

export default Navbar;
