import React, { useState, useEffect } from 'react';

const MovieTable = () => {
  const [movieData, setMovieData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://cinepedia-dsrv.onrender.com/api/movies');
        const data = await response.json();
        setMovieData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <div className="">
        <input type="text" className="" />
        <button className="">Search</button>
      </div>

      <div className="">
        {movieData && movieData.results ? (
          movieData.results.map((movie) => (

            <div className="w-1/2 md:w-1/3 lg:w-1/4 xl-1/4 px-4 mb-4" key={movie.movie_id}>
              <div className="bg-white p-4 rounded-lg shadow-md">
                <img src={movie.cover_img}  alt={movie.title} className="mb-4 rounded" />
                <h2 className="text-xl font-bold mb-2">{movie.title}</h2>
                <p className="text-gray-700">{movie.sinopsis}</p>
                <p className="text-gray-700">Director: {movie.director}</p>
                <p className="text-gray-700">Rating: {movie.rating}</p>
                <button className="bg-blue-500 text-white px-4 py-2 mt-4 rounded-full">Watch</button>
              </div>
            </div>

          ))
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default MovieTable;
