// MovieCard.js

import React from 'react';

const MovieCard = ({ title, imageSrc, description }) => {
  return (
    <div className="max-w-xs mx-auto overflow-hidden rounded shadow-lg h-72">
      <div className="aspect-w-2 aspect-h-3">
        <img className="object-cover w-full h-full" src={imageSrc} alt={title} />
      </div>
      <div className="px-6 py-4">
        <div className="font-bold text-xl mb-2 line-clamp-2">{title}</div>
        <p className="text-gray-700 text-base line-clamp-3">
          {description}
        </p>
      </div>
    </div>
  );
};

export default MovieCard;
