import React, { useState, useEffect } from 'react';

const MovieTable = () => {
  const [movieData, setMovieData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:2090/api/movies'); // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint
        const data = await response.json();
        setMovieData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div className="flex justify-center items-center h-screen">
      {movieData ? (
        <div className="max-w-sm rounded overflow-hidden shadow-lg">
          <img className="w-full" src={movieData.results[0].cover_img} alt={movieData.results[0].title} />
          <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{movieData.results[0].title}</div>
            <p className="text-gray-700 text-base">{movieData.results[0].sinopsis}</p>
          </div>
          <div className="px-6 py-4">
            <p className="text-gray-700 text-base">Casting: {movieData.results[0].casting}</p>
            <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700">
              Director: {movieData.results[0].director}
            </span>
            <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700">
              Rating: {movieData.results[0].rating}
            </span>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default MovieTable;
